import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';
import { buscarArtistasPorGenero } from '../../component/api/spotifyApi';

const ArtistasPorGenero = ({ genero }) => {
  const [artistas, setArtistas] = useState([]);

  useEffect(() => {
    const fetchArtistas = async () => {
      const response = await buscarArtistasPorGenero(genero);
      setArtistas(response.artists.items);
    };

    fetchArtistas();
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{genero}</Text>
      <View style={styles.artistList}>
        {artistas.map((artista) => (
          <View key={artista.id} style={styles.artist}>
            <Image source={{ uri: artista.images[0].url }} style={styles.artistImage} />
            <Text style={styles.artistName}>{artista.name}</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    margin: 16,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  artistList: {
    flexDirection: 'row',
    flexWrap: 'wrap',
  },
  artist: {
    width: '50%',
    alignItems: 'center',
    marginBottom: 16,
  },
  artistImage: {
    width: 150,
    height: 150,
    borderRadius: 75,
    marginBottom: 8,
  },
  artistName: {
    fontSize: 16,
    textAlign: 'center',
  },
});
