import axios from "axios";
const baseURL = "https://api.spotify.com/v1/";

const client = axios.create({
    baseURL: baseURL,
    timeout: 5000,
    headers: {
        "Authorization": "Bearer BQDagGWVwVT9g6Ek-w45yMZCE7nCHI_qMh6X41waWxvGUR4XCnK8cxXuCCivta3nCJUW-azF6X345qV-wsgbenVsApOgQ17xUf_yyNF_x0H5zlHWR6Ki"
    }
});

const getGeneros = async () => {
    const uri = encodeURI(`recommendations/available-genre-seeds`)
    return await client.get(uri)
}

const buscarArtistasPorGenero = async (genero) => {
  const uri = `search?type=artist&q=genre:${genero}`;
  const response = await client.get(uri);
  return response.data.artists.items;
}

export {getGeneros, buscarArtistasPorGenero};
