import React, { useState, useContext } from 'react';
import { StyleSheet, View, Text, TouchableOpacity, Image, FlatList } from 'react-native';
import { buscarArtistasPorGenero } from '../../component/api/spotifyApi';

const HomeScreen = ({ navigation, route }) => {
  const [resultados, setResultados] = useState([]);

  const buscarArtistas = async (genero) => {
    try {
      const data = await buscarArtistasPorGenero(genero);
      setResultados(data.artists.items);
    } catch (error) {
      console.log(error);
    }
  };

  const renderItem = ({ item }) => (
    <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('Detalhes', { artista: item })}>
      <Image source={{ uri: item.images[0].url }} style={styles.imagem} />
      <Text style={styles.texto}>{item.name}</Text>
    </TouchableOpacity>
  );

  const renderGenero = ({ item }) => (
    <TouchableOpacity style={[styles.genero, { backgroundColor: item.color }]} onPress={() => buscarArtistas(item.nome)}>
      <Text style={styles.texto}>{item.nome}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={route.params.selectedGenres}
        renderItem={renderGenero}
        keyExtractor={(item) => item.id}
        horizontal={true}
      />
      <FlatList
        data={resultados}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        style={{ flex: 1 }}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
  },
  genero: {
    padding: 10,
    borderRadius: 10,
    marginRight: 10,
  },
  card: {
    flex: 1,
    borderRadius: 10,
    overflow: 'hidden',
    margin: 5,
  },
  imagem: {
    height: 150,
    resizeMode: 'cover',
  },
  texto: {
    fontSize: 16,
    fontWeight: 'bold',
    margin: 5,
  },
});

export default HomeScreen;
