import React, { useContext } from 'react';
import { StyleSheet, View, Text } from 'react-native';
import ThemeContext from '../../context/context';
import AppTheme from '../../component/themes/themes';


const HomeScreen = ({ navigation, route }) => {
  const selectedGenres = route.params.selectedGenres;
  const theme = useContext(ThemeContext)[0];

  return (
    <View style={[styles.container,AppTheme[theme+'Container']]}>
        <View>
            <Text style={styles.title}>Seus gÃªneros selecionados:</Text>
            <View style={styles.genreList}>
                {selectedGenres.map((genre) => (
                    <Text key={genre} style={styles.genre}>{genre}</Text>
                    ))}
            </View>
        </View>    
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  genreList: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  genre: {
    padding: 8,
    margin: 8,
    borderRadius: 4,
    backgroundColor: '#f2f2f2',
    fontSize: 16,
  },
});

export default HomeScreen;
