import React, { useState, useContext } from 'react';
import { StyleSheet, View, Text, TouchableOpacity } from 'react-native';
import ThemeContext from '../../context/context';
import AppTheme from '../../component/themes/themes';
import SearchResults from '../../component/searchResults/searchResults';
import { buscarArtistasPorGenero } from '../../component/api/spotifyApi';

const HomeScreen = ({ navigation, route }) => {
const [searchResults, setSearchResults] = useState([]);
const selectedGenres = route.params.selectedGenres;
const theme = useContext(ThemeContext)[0];

const handleGenreClick = async (genre) => {
try {
const response = await buscarArtistasPorGenero(genre);
setSearchResults(response.artists.items);
} catch (error) {
console.log(error);
}
};

return (
<View style={[styles.container, AppTheme[theme + 'Container']]}>
<View style={styles.container}>
<Text style={styles.title}>Seus gÃªneros selecionados:</Text>
<View style={styles.genreList}>
{selectedGenres.map((genre) => (
<TouchableOpacity
key={genre}
onPress={() => handleGenreClick(genre)}
>
<Text style={styles.genre}>{genre}</Text>
</TouchableOpacity>
))}
</View>
<SearchResults searchResults={searchResults} />
</View>
</View>
);
};

const styles = StyleSheet.create({
container: {
flex: 1,
alignItems: 'center',
justifyContent: 'center',
},
title: {
fontSize: 24,
fontWeight: 'bold',
marginBottom: 16,
},
genreList: {
flexDirection: 'row',
flexWrap: 'wrap',
justifyContent: 'center',
},
genre: {
padding: 8,
margin: 8,
borderRadius: 4,
backgroundColor: '#f2f2f2',
fontSize: 16,
},
});

export default HomeScreen;
