import React, { useState, useContext } from 'react';
import { StyleSheet, View, Text, TouchableOpacity, ScrollView, Image } from 'react-native';
import ThemeContext from '../../context/context';
import AppTheme from '../../component/themes/themes';
import { buscarArtistasPorGenero } from '../../component/api/spotifyApi';

const HomeScreen = ({ navigation, route }) => {
  const { theme } = useContext(ThemeContext);
  const { selectedGenres } = route.params;
  const [artists, setArtists] = useState([]);

  const handleGenrePress = async (genre) => {
    const result = await buscarArtistasPorGenero(genre);
    setArtists(result.artists.items);
  };

  return (
    <View style={[styles.container, { backgroundColor: AppTheme[theme].backgroundColor }]}>
      <ScrollView contentContainerStyle={styles.scrollView}>
        {selectedGenres.map((genre, index) => (
          <TouchableOpacity
            key={index}
            style={[styles.card, { backgroundColor: AppTheme[theme].cardColor }]}
            onPress={() => handleGenrePress(genre)}
          >
            <Text style={[styles.cardTitle, { color: AppTheme[theme].textColor }]}>{genre}</Text>
          </TouchableOpacity>
        ))}
        <SearchResults results={artists}>
          {({ name, images }) => (
            <TouchableOpacity key={name} style={styles.artistCard}>
              <Image style={styles.artistImage} source={{ uri: images[0].url }} />
              <Text style={styles.artistName}>{name}</Text>
            </TouchableOpacity>
          )}
        </SearchResults>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scrollView: {
    padding: 16,
  },
  card: {
    height: 80,
    borderRadius: 8,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 16,
  },
  cardTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  artistCard: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 8,
  },
  artistImage: {
    height: 60,
    width: 60,
    borderRadius: 30,
    marginRight: 16,
  },
  artistName: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default HomeScreen;
