import axios from "axios";

const baseURL = "https://api.spotify.com/v1/";

const client = axios.create({
    baseURL: baseURL,
    timeout: 5000,
    headers: {
        "Authorization": "Bearer BQBNRn6wtXvAzx4dOtEKJ3clOhag9kntV25UPYsmN1VAtuD6O93mkv_kCVNi928q3ca6NPy_piVrBR_2fWEQi6ox-Q1td5yhJt9AWbHNvUYpyxrHTIgr"
    }
});

const getGeneros = async (url) => {
    const uri = encodeURI(`available-genre-seeds`)
    return await client.get(uri)
}

export {getGeneros}
