import React, { useEffect, useState, useRef } from 'react';
import { StyleSheet, View, Text, Image, TouchableOpacity, Alert, ScrollView } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { getGeneros } from '../../component/api/spotifyApi';
import ThemeToogler from '../../component/themeToogler/themeToogler';


const PreferenciasScreen = () => {
  const [fetchResult, setFetchResult] = useState({ genres: [] });
  const [selectedGenres, setSelectedGenres] = useState([]);
  const buttonRef = useRef(null);

  useEffect(() => {
    fetchPreferences();
  }, []);

  async function fetchPreferences() {
    try {
      const { data: { genres } } = await getGeneros();
      setFetchResult({ genres });
      console.log(genres);
    } catch (error) {
      setFetchResult({ genres: [] });
      console.error(error);
    }
  }

  function handleGenreSelect(genre) {
    if (selectedGenres.includes(genre)) {
      setSelectedGenres(selectedGenres.filter((selectedGenre) => selectedGenre !== genre));
    } else if (selectedGenres.length < 5) {
      setSelectedGenres([...selectedGenres, genre]);
    }
  }

  async function handleSavePreferences() {
    if (selectedGenres.length === 0) {
      Alert.alert('Erro', 'Selecione pelo menos um gênero.');
      return;
    }

    try {
      await AsyncStorage.setItem('selectedGenres', JSON.stringify(selectedGenres));
      Alert.alert('Sucesso', 'Preferências salvas com sucesso!');
    } catch (error) {
      console.error(error);
      Alert.alert('Erro', 'Ocorreu um erro ao salvar as preferências.');
    }
  }

  function handleScroll(event) {
    const buttonHeight = buttonRef.current.offsetHeight;
    const buttonWidth = buttonRef.current.offsetWidth;
    const windowSize = event.nativeEvent.layoutMeasurement.height;
    const contentHeight = event.nativeEvent.contentSize.height;
    const currentPosition = event.nativeEvent.contentOffset.y;

    if (currentPosition + windowSize >= contentHeight - buttonHeight) {
      buttonRef.current.setNativeProps({
        style: {
          position: 'absolute',
          bottom: 32,
          right: 32,
        }
      });
    } else {
      buttonRef.current.setNativeProps({
        style: {
          position: 'relative',
        }
      });
    }
  }

  return (
    <View style={styles.container}>
        <ThemeToogler/>
      <ScrollView contentContainerStyle={styles.scrollContainer} onScroll={handleScroll}>
        {fetchResult.genres.map((genre) => (
          <TouchableOpacity
            key={genre}
            style={[styles.genreCard, selectedGenres.includes(genre) && styles.selectedGenreCard]}
            onPress={() => handleGenreSelect(genre)}
            activeOpacity={0.7}
          >
            <Text style={[styles.genreText, selectedGenres.includes(genre) && styles.selectedGenreText]}>{genre}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>
      <TouchableOpacity ref={buttonRef} style={styles.button} onPress={handleSavePreferences}>
        <Text style={styles.buttonText}>Pronto</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
    container: {
    flex: 1,
    backgroundColor: '#fff',
    
    },
    scrollContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    padding: 16,
    },
    genreCard: {
    backgroundColor: '#F1F1F1',
    borderRadius: 16,
    paddingVertical: 8,
    paddingHorizontal: 16,
    margin: 8,
    },
    selectedGenreCard: {
    backgroundColor: '#1DB954',
    },
    genreText: {
    fontSize: 16,
    color: '#333',
    },
    selectedGenreText: {
    color: '#fff',
    },
    button: {
    position: 'absolute',
    bottom: 5,
    right: 5,
    backgroundColor: '#1DB954',
    borderRadius: 32,
    paddingVertical: 12,
    paddingHorizontal: 24,
    maxHeight: 45,
    maxWidth: 99.55
    },
    buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
    },
    });

export default PreferenciasScreen;
